INSERT INTO `mg_company_info` VALUES ('143', null, '上海逸橙信息科技有限公司', 'e成', '人力资源管理软件', '上海市黄浦区 马当路388号SOHO复兴广场B座3F', '021-63081616', 'http://www.ifchange.com', 'e成，基于算法的招聘服务SAAS平台， 立足招聘，成为提供职场大数据服务的公司。B端——整合全渠道招聘资源，以高效简历获取、意向确认服务为切入点，并立足自身优势，不断开放合作，打造一站式的HR云服务平台，成为中国的”Workday”。C端——中国最大的职位搜索引擎，以全网职位搜索及求职大数据分析切入C端市场，打造中国特色“Indeed“，提高HR和求职者的沟通效率，发展为职场消费流量入口。', '201702/logo_201702071039039.png', '201702/compress_logo_201702071039039.png', null, null, null, 'June', '2017-02-07 10:39:39', null, null);
