INSERT INTO `mg_company_info` VALUES ('301', '0206a5649b1944f76e847cbef225398e', '领族网络技术（上海）有限公司', '领族网', '人才寻猎', '上海市徐汇区斜土路2669号英雄大厦11楼', '021-51827680', 'http://www.chrstaff.com/', '领族网，国内注册名称：领族网络技术（上海）有限公司，由人力资源行业资深职业经理共同打造，以候选人为中心，人力资源服务模式创新，业界领先的人力资源综合解决方案提供商，拥有RPO招聘流程外包、BPO商务流程外包、HRO人才全风险外包，中高级人才寻猎、国内外校园招聘，招聘管理系统和候选人测评系统帮助企业甄选精英；致力于协助企业建立中、高端人才团队，公司以网络产品依托，为互联网、移动互联网用户提供更方便的一体化人才资讯平台，兴起人力资源行业新时代创新性的革命，励志成为人力资源行业创新引导者。', '201702/logo_201702071517049.png', '201702/compress_logo_201702071517049.png', '201702/qrCode_201702071517049.jpg', '201702/compressQrCode_201702071517049.jpg', null, 'lijun', '2017-02-07 15:14:20', 'lijun', '2017-02-07 15:17:49');
