INSERT INTO `mg_company_info` VALUES ('310', '8773c7ff6949f45d5178b4247671d258', '扬州博达人力资源管理有限公司', '扬州博达管理', '人才寻猎', null, null, 'http://www.yzbdgl.com/', '扬州博达人力资源管理有限公司是新型人力资源管理公司，公司创始人秉承“专业服务、创新发展、合作共赢”的理念，致力为企业提供优质的人力资源服务。博达业务除岗位招聘、人事代理、员工拓展、员工培训等常规项目，更重点核心打造集人力资源配置、组织架构优化、人才梯队建设、企业文化建设为一体的企业专属定制化人力资源管理方案。通过引入最新的管理理念，制定最适合的管理办法，帮助企业提升管理水平，做好企业战略的人力资源服务。打造有价值的人力资源品牌是公司坚持不懈的方向。', '201702/logo_201702071532022.png', '201702/compress_logo_201702071532022.png', null, null, null, 'lijun', '2017-02-07 15:32:22', null, null);
