INSERT INTO `mg_company_info` VALUES ('480', 'af094b4bfbfe8df7ac4f5e32d390296c', '上海西晓投资中心（有限合伙）', '人力窝', '人力资源管理软件', null, '021-35883215', 'http://www.wowoohr.com', 'WoWooHR是一个专注于为中小微企业，以及中国广大在职者和求职者，提供人力资源服务的互联网平台。基于互联网的思想和技术、30余年中国人力资源服务的经验，WoWooHR致力于打造一个开放的行业生态，与全国人力资源服务机构一起，共同为发展中的企业和个人提供服务支持。', '201702/logo_201702081106040.png', '201702/compress_logo_201702081106040.png', '201702/qrCode_201702081106040.png', '201702/compressQrCode_201702081106040.png', null, 'June', '2017-02-08 11:03:31', 'June', '2017-02-08 11:06:40');
